from lib.db.primary_key import Base, sa

vendor_state_association = sa.Table(
    "vendor_state_ass",
    Base.metadata,
    sa.Column(
        "vendor_id",
        sa.ForeignKey(
            "vendor.id",
            onupdate="CASCADE",
            ondelete="CASCADE",
        ),
        primary_key=True,
    ),
    sa.Column(
        "state_id",
        sa.ForeignKey(
            "state.id",
            onupdate="CASCADE",
            ondelete="CASCADE",
        ),
        primary_key=True,
    ),
)
